package com.ri;

import com.ri.meta.*;

import java.io.PrintWriter;
import java.math.BigDecimal;

public class C_Associated_Fundamental_Constants {
    // @formatter:off
    // 1000 decimal digit precision fractional part
    public static final BigDecimal EULER_MASCHERONI_CONSTANT = new BigDecimal("0.");
    public static final BigDecimal GOLDEN_RATIO              = new BigDecimal("0.");
    public static final BigDecimal E                         = new BigDecimal("0.");
    public static final BigDecimal PI                        = new BigDecimal("0.");
    public static final BigDecimal FIRST_FEIGENBAUM_CONSTANT = new BigDecimal("0.");
    public static final BigDecimal GAMMA_ONE_SIXTH           = new BigDecimal("0.");
    // @formatter:on


    @SuppressWarnings("UnnecessaryLocalVariable")
    public static void main(String[] args) throws Exception {
        // @formatter:off
        String          _clazzName  = C_Associated_Fundamental_Constants.class.getSimpleName();
        ProjectType     type        = ProjectType.DATA;
        ProjectPD       pd          = ProjectPD.LEFT;
        ProjectCategory category    = ProjectCategory.SIGNATURE;
        byte            id          = (byte) 0x3c;
        String          name        = _clazzName;
        ProjectState    state       = ProjectState.SUCCESS;
        // @formatter:on

        ProjectName projectName = new ProjectName(type, pd, category, id, name, state);
        Projects.getInstance().checkName(projectName);

        System.out.print("================================ ");
        System.out.println(projectName.getFullName());

        execute(projectName);

        System.out.println();
        System.out.println("SUCCESS");
    }

    private static void execute(ProjectName projectName) throws Exception {
        try (PrintWriter out = new PrintWriter(projectName.getFile(".txt"))) {
            out.println("0 (Euler-Mascheroni constant) / C4");
            out.println(convertToBase16(EULER_MASCHERONI_CONSTANT));
            out.println("1 (Golden Ratio) / C2");
            out.println(convertToBase16(GOLDEN_RATIO));
            out.println("2 (Euler's number) / C0");
            out.println(convertToBase16(E));
            out.println("3 (Pi) / C3");
            out.println(convertToBase16(PI));
            out.println("4 (First Feigenbaum Constant) / C5");
            out.println(convertToBase16(FIRST_FEIGENBAUM_CONSTANT));
            out.println("5 (Gamma of 1/6) / C1");
            out.println(convertToBase16(GAMMA_ONE_SIXTH));
        }
    }

    public static String convertToBase16(BigDecimal decimalNumber) {
        BigDecimal fractionalPart = decimalNumber;

        if (fractionalPart.intValue() != 0) throw new RuntimeException();

        BigDecimal base16 = BigDecimal.valueOf(16);

        StringBuilder fracResult = new StringBuilder();
        for (int i = 0; i < 128; i++) {
            fractionalPart = fractionalPart.multiply(base16);
            int digit = fractionalPart.intValue();
            fracResult.append(Integer.toHexString(digit));
            fractionalPart = fractionalPart.subtract(BigDecimal.valueOf(digit));

            if (i % 32 == 31) fracResult.append('\n');
            else if (i % 8 == 7) fracResult.append("  ");
            else if (i % 4 == 3) fracResult.append(' ');
        }

        return fracResult.toString();
    }
}
